mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PWD = #{memberPwd}
AND STATUS = 'Y'

mapper.insert:
INSERT INTO MEMBER(
					MEMBER_NO
					,MEMBER_ID
					,MEMBER_PWD
					,MEMBER_NAME
					,MEMBER_BIRTH
					,GENDER
					,ADDRESS
					,EMAIL
					,PHONE
					)
		VALUES( 
					SEQ_MNO.NEXTVAL
			       ,#{memberId}
			       ,#{memberPwd}
			       ,#{memberName}
			       ,#{memberBirth}
			       ,#{gender}
			       ,#{address}
			       ,#{email}
			       ,#{phone}
				)
